mapper.select:
SELECT *
FROM lesson_grades
WHERE schedule_id = #{scheduleId}
  AND student_id = #{studentId}
  AND is_deleted = 0

SELECT lg.id, lg.schedule_id, lg.student_id, s.real_name AS student_name, sch.teacher_id, t.real_name AS teacher_name, sch.course_id, c.title AS course_title, sub.name AS subject_name, sch.scheduled_date, sch.start_time, sch.end_time, lg.score, lg.teacher_comment, lg.graded_at, lg.created_at, lg.updated_at FROM lesson_grades lg LEFT JOIN schedules sch ON lg.schedule_id = sch.id AND sch.is_deleted = 0 LEFT JOIN students s ON lg.student_id = s.id AND s.is_deleted = 0 LEFT JOIN teachers t ON sch.teacher_id = t.id AND t.is_deleted = 0 LEFT JOIN courses c ON sch.course_id = c.id AND c.is_deleted = 0 LEFT JOIN subjects sub ON c.subject_id = sub.id AND sub.is_deleted = 0 WHERE lg.is_deleted = 0 AND lg.student_id = #{studentId}
AND sub.name = #{subjectName}
AND sch.scheduled_date >= #{startDate}
AND sch.scheduled_date <= #{endDate}
ORDER BY sch.scheduled_date DESC, sch.start_time DESC

SELECT lg.id, lg.schedule_id, lg.student_id, s.real_name AS student_name, sch.teacher_id, t.real_name AS teacher_name, sch.course_id, c.title AS course_title, sub.name AS subject_name, sch.scheduled_date, sch.start_time, sch.end_time, lg.score, lg.teacher_comment, lg.graded_at, lg.created_at, lg.updated_at FROM lesson_grades lg LEFT JOIN schedules sch ON lg.schedule_id = sch.id AND sch.is_deleted = 0 LEFT JOIN students s ON lg.student_id = s.id AND s.is_deleted = 0 LEFT JOIN teachers t ON sch.teacher_id = t.id AND t.is_deleted = 0 LEFT JOIN courses c ON sch.course_id = c.id AND c.is_deleted = 0 LEFT JOIN subjects sub ON c.subject_id = sub.id AND sub.is_deleted = 0 WHERE lg.is_deleted = 0 AND sch.teacher_id = #{teacherId}
AND s.real_name LIKE CONCAT('%', #{studentName}, '%')
AND sub.name = #{subjectName}
AND sch.scheduled_date >= #{startDate}
AND sch.scheduled_date <= #{endDate}
ORDER BY sch.scheduled_date DESC, sch.start_time DESC
