mapper.select:
SELECT rr.* FROM reschedule_requests rr INNER JOIN schedules s ON rr.schedule_id = s.id WHERE s.teacher_id = #{teacherId} AND rr.is_deleted = 0
AND rr.status = #{status}
ORDER BY rr.created_at DESC

SELECT * FROM reschedule_requests
WHERE applicant_id = #{applicantId}
  AND applicant_type = #{applicantType}
  AND is_deleted = 0

    AND status = #{status}

ORDER BY created_at DESC
