mapper.select:
SELECT rr.* FROM reschedule_requests rr INNER JOIN course_schedules cs ON rr.schedule_id = cs.id INNER JOIN course_enrollments ce ON cs.enrollment_id = ce.id WHERE ce.teacher_id = #{teacherId} AND rr.is_deleted = 0
AND rr.status = #{status}
ORDER BY rr.created_at DESC

SELECT * FROM reschedule_requests
WHERE applicant_id = #{applicantId}
  AND applicant_type = #{applicantType}
  AND is_deleted = 0

    AND status = #{status}

ORDER BY created_at DESC

SELECT * FROM reschedule_requests WHERE is_deleted = 0
AND status = #{status}
ORDER BY created_at ASC

SELECT rr.* FROM reschedule_requests rr INNER JOIN course_schedules cs ON rr.schedule_id = cs.id INNER JOIN course_enrollments ce ON cs.enrollment_id = ce.id WHERE ce.student_id = #{studentId} AND rr.is_deleted = 0
AND rr.status = #{status}
ORDER BY rr.created_at DESC
